''' Partitioning string into palindromes
'''

def minCut(s):
    if not s: return 0
    n = len(s)
    if 1 == n: return 0

    from collections import defaultdict
    char_idx = defaultdict(list)
    tbl_npart = []  # good old array is much faster

    # Construct palindrome table
    for i in range(0, n):
        tbl_npart.append([None] * n)
        tbl_npart[i][i] = 0
        palin_inds = char_idx[s[i]]
        char_idx[s[i]].append(i)
        for t in palin_inds:
            if t + 1 >= i or 0 == tbl_npart[t+1][i-1]:
                tbl_npart[t][i] = 0
    
    # Non-pro-active DP with memoization
    def min_npart(i, j):
        rec = tbl_npart[i][j]
        if rec is not None: return rec

        ch = s[i]
        ch_inds = [t for t in char_idx[ch]
                   if i <= t and t < j and 0 == tbl_npart[i][t]]
        min_tot = j - i
        for t in ch_inds:
            min_tot = min(min_tot, 1 + min_npart(t+1, j))
        tbl_npart[i][j] = min_tot
        return min_tot

    return min_npart(0, n-1)


def TEST(s):
    print('-----------------')
    print(s)
    print(minCut(s))
    

TEST('aaabbaa')
TEST("")
TEST("")
TEST("")
